mapper.select:
SELECT SQL_CALC_FOUND_ROWS A.ADMISSION_ID -- 입소내역ID , A.CENTER_ID -- 센터ID , TC.CENTER_NM -- 센터명 , P.PATIENT_ID -- 환자ID , P.PATIENT_NM -- 환자명 , A.ADMISSION_DATE -- 시작일 , A.ROOM -- 호실 , ROOM.DETAIL_CD_NM AS ROOM_NM -- 호실명 , A.QANTN_DIV -- 격리/입소 구분(CD004) , TIMESTAMPDIFF(DAY, A.ADMISSION_DATE, NOW()) AS QANTN_DAY -- 격리일수 , CASE WHEN A.DSCHGE_DATE IS NULL THEN '1' ELSE '2' END AS QANTN_STATUS -- 입소/격리 상태 , CASE WHEN VI.ADMISSION_ID IS NULL THEN 'X' ELSE 'O' END AS VIDEO_DOWN , '' AS AI_EXE FROM ADMISSION A INNER JOIN PATIENT P ON A.PATIENT_ID = P.PATIENT_ID INNER JOIN TREATMENT_CENTER TC ON A.CENTER_ID = TC.CENTER_ID LEFT JOIN COM_CD_DETAIL ROOM ON ROOM.COM_CD = 'CD005' AND ROOM.DETAIL_CD = A.ROOM AND ROOM.PROPERTY1 = TC.CENTER_ID LEFT JOIN ( SELECT DISTINCT ADMISSION_ID FROM ARCHIVE_DOWN ) VI ON A.ADMISSION_ID = VI.ADMISSION_ID WHERE A.CENTER_ID = #{centerId} -- 센터ID AND A.QANTN_DIV = '2' -- 생활치료센터 입소자 구분 AND A.DEL_YN = 'N'
AND P.PATIENT_ID = #{patientId}
AND P.PATIENT_NM LIKE CONCAT('%', #{patientNm},'%')
AND A.DSCHGE_DATE IS NULL
AND A.DSCHGE_DATE IS NOT NULL
ORDER BY
${orderBy} ${orderDir}
A.DSCHGE_DATE is null desc,A.ADMISSION_DATE DESC
LIMIT #{recordCountPerPage} OFFSET #{offsetCount}

mapper.insert:

-- 입소내역 생성
-- insertAdmission
INSERT
  INTO ADMISSION
     ( ADMISSION_ID
     , PATIENT_ID
     , ADMISSION_DATE
     , DSCHGE_SCHDLD_DATE
     , QANTN_DIV
     , PERSON_CHARGE
     , CENTER_ID
     , ROOM
     , REG_ID
     , UPD_ID
     )
VALUES
     ( #{admissionId}
     , #{patientId}
     , #{admissionDate}
     , #{dschgeSchdldDate}
     , #{qantnDiv}
     , #{personCharge}
     , #{centerId}
     , #{room}
     , #{regId}
     , #{regId}
     )

mapper.update:

-- 입소내역 수정
-- updateAdmission
UPDATE ADMISSION 
   SET ADMISSION_DATE     = #{admissionDate}
     , DSCHGE_SCHDLD_DATE = #{dschgeSchdldDate}
     , PERSON_CHARGE      = #{personCharge}
     , CENTER_ID          = #{centerId}
     , ROOM               = #{room}
     , UPD_ID             = #{updId}
     , UPD_DT             = NOW()
 WHERE ADMISSION_ID = #{admissionId}


-- 퇴소처리
-- updateAdmissionDischarge
UPDATE ADMISSION 
   SET DSCHGE_DATE = #{dschgeDate}
   	 , UPD_ID      = #{updId}
     , UPD_DT      = NOW()
     , QANTN_LOCATION=#{quantLocation}
 WHERE ADMISSION_ID = #{admissionId}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS A.ADMISSION_ID -- 입소내역ID , P.PATIENT_ID -- 환자ID , P.PATIENT_NM -- 환자명 , A.QANTN_DIV -- 격리/입소 구분(CD004) , A.ADMISSION_DATE -- 격리 시작일 , TIMESTAMPDIFF(DAY, A.ADMISSION_DATE, NOW()) AS QANTN_DAY -- 격리일수 , CASE WHEN A.DSCHGE_DATE IS NULL THEN '1' ELSE '2' END AS QANTN_STATUS -- 입소/격리 상태 , CASE WHEN VI.ADMISSION_ID IS NULL THEN 'X' ELSE 'O' END AS VIDEO_DOWN , '' AS AI_EXE FROM ADMISSION A INNER JOIN PATIENT P ON A.PATIENT_ID = P.PATIENT_ID LEFT JOIN ( SELECT DISTINCT ADMISSION_ID FROM ARCHIVE_DOWN ) VI ON A.ADMISSION_ID = VI.ADMISSION_ID WHERE A.QANTN_DIV = '1' -- 자가격리자 구분 AND A.DEL_YN = 'N'
AND P.PATIENT_ID = #{patientId}
AND P.PATIENT_NM LIKE CONCAT('%', #{patientNm},'%')
AND A.DSCHGE_DATE IS NULL
AND A.DSCHGE_DATE IS NOT NULL
ORDER BY
${orderBy} ${orderDir}
A.DSCHGE_DATE is null desc, A.ADMISSION_DATE DESC
LIMIT #{recordCountPerPage} OFFSET #{offsetCount}
