mapper.insert:
        /** repository.DrugDao.insertDrug.insertDrug */
		INSERT
		  INTO drug
             ( DRUG_SEQ
             , ADMISSION_ID
             , NOTICE_START_DATE
             , NOTICE_END_DATE
             , NOTICE_DATE
             , NOTICE_NAME
             , REG_DT
             )
        VALUES
             ( #{drugSeq}
             , #{admissionId}
             , #{noticeStartDate}
             , #{noticeEndDate}
             , #{noticeDate}
             , #{noticeName}
             , NOW()
             )

/** repository.DrugDao.insertDrug.insertDrugAlarm */
INSERT
INTO drug_list
(   DRUG_SEQ
    , DRUG_NAME
    , DRUG_COUNT
    , DRUG_TYPE
, REG_DT
)
VALUES
    (
      #{drugSeq}
    , #{drugName}
    , #{drugCount}
    , #{drugType}
    , now()
    )

        /** repository.DrugDao.insertDrug.insertDrugAlarm */
		INSERT
		  INTO drug_alarm
             (   DRUG_ALARM_SEQ
               , DRUG_SEQ
               , NOTICE_TIME
               , REG_DT
             )
        VALUES
             (   #{drugAlarmSeq}
               , #{drugSeq}
               , #{noticeTime}
               , now()
             )

/** repository.DrugDao.insertDrug.insertDrugDose */
INSERT
    INTO drug_dose
    (  DRUG_DOSE_SEQ
     , ADMISSION_ID
     , NOTICE_DD
     , NOTICE_TIME
     , NOTICE_NAME
     , DRUG_NAME
     , DRUG_COUNT
     , DRUG_TYPE
     , TAKE_RESULT
     , REG_DT
     , DRUG_SEQ
     , DRUG_ALARM_SEQ
     , NO_ALARM
    )
VALUES
    ( #{drugDoseSeq}
    , #{admissionId}
    , #{noticeDd}
    , #{noticeTime}
    , #{noticeName}
    , #{drugName}
    , #{drugCount}
    , #{drugType}
    , #{takeResult}
    , now()
    , #{drugSeq}
    , #{drugAlarmSeq}
    , #{noAlarm}
    )

mapper.select:
select
      da.DRUG_NAME
     ,da.DRUG_COUNT
     ,da.DRUG_TYPE
from drug_list da
         inner join drug d
                    on da.DRUG_SEQ  = d.DRUG_SEQ
where 1=1
  and da.DRUG_SEQ = #{drugSeq}
  and d.ADMISSION_ID =  #{admissionId}
  and d.NOTICE_START_DATE  <=  #{requestDate}
  and d.NOTICE_END_DATE    >=  #{requestDate}
