mapper.select:
SELECT ITEM_ID -- 측정항목 ID , ITEM_NM -- 측정항목명 , UNIT -- 측정단위 , REF_FROM -- 참고치 From , REF_TO -- 참고치 TO , DEL_YN -- 삭제여부 , REG_ID , REG_DT , UPD_ID , UPD_DT FROM ITEM
WHERE DEL_YN = 'N' AND ITEM_ID = #{itemId}
ORDER BY ITEM_ID

mapper.insert:

		INSERT
		  INTO ITEM
		     ( ITEM_ID		  -- 측정항목 ID
			 , ITEM_NM          -- 측정항목명
			 , UNIT             -- 측정단위
			 , REF_FROM         -- 참고치 From
			 , REF_TO           -- 참고치 To
			 , REG_ID           -- 등록자ID
			 , UPD_ID           -- 수정자ID
		     )
	  VALUES
	         ( #{itemId}
             , #{itemNm}
			 , #{unit}
			 , #{refFrom}
			 , #{refTo}
			 , #{regId}
			 , #{regId}
			 )
