mapper.select:

		SELECT PATIENT_ID		    -- 환자ID
			 , LOGIN_ID             -- 로그인ID
			 , PASSWORD             -- 비밀번호
			 , PATIENT_NM           -- 성명
			 , BIRTH_DATE           -- 생년월일
			 , SEX                  -- 성별
			 , CELL_PHONE           -- 휴대폰
		     , GUARDIAN_CELL_PHONE  -- 보호자연락처
			 , ZIP_CODE             -- 우편번호
			 , ADDRESS1             -- 주소
			 , ADDRESS2             -- 상세주소
		     , SEARS_ACCOUNT        -- 시어스 체온계 계정
             , ACTIVE_STATUS
		     , MEMO
		  FROM PATIENT
		 WHERE PATIENT_NM = #{patientNm}
		   AND BIRTH_DATE = #{birthDate}
		   AND SEX        = #{sex}
		   AND CELL_PHONE = #{cellPhone}
		   AND SEARS_ACCOUNT = #{searsAccount}
		   AND MEMO = #{memo}
		   AND ACTIVE_STATUS = #{activeStatus}



            AND SEARS_ACCOUNT = #{searsAccount}

mapper.update:

UPDATE PATIENT
   SET LOGIN_ID             = #{loginId}
     , PASSWORD             = #{password}
     , PATIENT_NM           = #{patientNm}
     , BIRTH_DATE           = #{birthDate}
     , SEX                  = #{sex}
     , CELL_PHONE           = #{cellPhone}
     , GUARDIAN_CELL_PHONE  = #{guardianCellPhone}
     , ZIP_CODE             = #{zipCode}
     , ADDRESS1             = #{address1}
     , ADDRESS2             = #{address2}
     , SEARS_ACCOUNT        = #{searsAccount}
	 , UPD_DT               = NOW()
 WHERE PATIENT_ID = #{patientId};


UPDATE PATIENT
   SET CELL_PHONE           = #{cellPhone}
     , GUARDIAN_CELL_PHONE  = #{guardianCellPhone}
     , ZIP_CODE             = #{zipCode}
     , ADDRESS1             = #{address1}
     , ADDRESS2             = #{address2}
     , UPD_DT               = NOW()
 WHERE PATIENT_ID = #{patientId};


UPDATE PATIENT
SET PASSWORD = #{password}
  , UPD_DT   = NOW()
WHERE LOGIN_ID = #{loginId}

mapper.insert:
INSERT
  INTO PATIENT
     ( PATIENT_ID
     , PATIENT_NM
     , BIRTH_DATE
     , SEX
     , CELL_PHONE
     , SEARS_ACCOUNT
     , ACTIVE_STATUS
     , MEMO
     )
VALUES
     ( #{patientId}
     , #{patientNm}
     , #{birthDate}
     , #{sex}
     , #{cellPhone}
     , #{searsAccount}
     , #{activeStatus}
     , #{memo}
     )

mapper.update:

-- 환자정보 수정 
-- updatePatient
UPDATE PATIENT 
   SET PATIENT_NM = #{patientNm}
     , BIRTH_DATE = #{birthDate}
     , SEX        = #{sex}
     , CELL_PHONE = #{cellPhone}
     , UPD_DT     = NOW()
     , SEARS_ACCOUNT = #{searsAccount}
     , MEMO = #{memo}
     , ACTIVE_STATUS = #{activeStatus}
 WHERE PATIENT_ID = #{patientId}
