mapper.select:

SELECT B.RESULT_DATE
     , B.RESULT_TIME
     , C.RESULT AS RESULT_DBP
     , D.RESULT AS RESULT_SBP
FROM ITEM A
         INNER JOIN RESULT B
                    ON A.ITEM_ID = B.ITEM_ID
         INNER JOIN result_detail C
                    ON B.RESULT_SEQ = C.RESULT_SEQ
         INNER JOIN result_detail D
                    ON B.RESULT_SEQ = D.RESULT_SEQ
WHERE A.ITEM_ID = #{itemId}
  AND B.ADMISSION_ID = #{admissionId}
  AND B.RESULT_DATE = #{resultDate}
  AND C.RESULT_TYPE = #{firstResultType}
  ANd D.RESULT_TYPE = #{secondResultType}
