mapper.select:
SELECT A.CENTER_ID -- 센터 id , A.CENTER_NM -- 센터명 , A.CENTER_LOCATION -- 센터위치 , A.HOSPITAL_CD -- 병원코드 , B.DETAIL_CD_NM AS HOSPITAL_NM -- 병원명 , A.USE_YN -- 사용여부 , A.REG_ID , A.REG_DT , A.UPD_ID , A.UPD_DT FROM TREATMENT_CENTER A LEFT JOIN COM_CD_DETAIL B ON B.COM_CD = 'CD002' AND B.DETAIL_CD = A.HOSPITAL_CD WHERE A.USE_YN = #{useYn}
AND A.CENTER_ID = #{centerId}
AND A.CENTER_NM LIKE CONCAT('%', #{centerNm}, '%')
AND B.DETAIL_CD_NM LIKE CONCAT('%', #{hospitalNm}, '%')
ORDER BY A.CENTER_NM

mapper.insert:

INSERT INTO
    treatment_center (
		     CENTER_ID                 -- 센터id
		   , CENTER_NM                 -- 센터명
		   , CENTER_LOCATION           -- 센터위치
		   , HOSPITAL_CD               -- 병원코드
		   , REG_ID                    -- 생성자
		   , UPD_ID					   -- 수정자
    )
	VALUES (
			 #{centerId}
		   , #{centerNm}
		   , #{centerLocation}
		   , #{hospitalCd}
		   , #{regId}
		   , #{regId}
	)
